package day04_aritmeticOperation;

public class additionConcat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Number+Number=Addition 
		System.out.println(2+2);
		System.out.println(20+200);
		
		//Number+String=Concatenation
		System.out.println(5+"Hello");
		
		//String+Boolen=Concatenation
		System.out.println("Hello" + true);
		
		//Number + Number :Addition
		System.out.println(2+2);
		System.out.println(20+200);
		
		//Number+String : Concatenation
		System.out.println(5 + "hello");
		
		//String+Boolean : Concetenation
		System.out.println("hello" + true);
		
		//String+String : Concetenation
		System.out.println("hello" + "world");
		
		//Boolean+Number
		//System.out.println(true+1);
		
		System.out.println((2+0+3+"Cybertek"));
		System.out.println("Cybertek" +2+0+5); //Önemliiii
		System.out.println(2+0+5+"Cybcertek"+2+0+5); //String öncesi toplar ama sonrasi yanyana yazar. 
		System.out.println(2+0+5+"Cybertek"+(2+0+1)); //Fark burda, sonrasini top icin paranteze almali önemli
		
		
		
		

	}

}
